mapper.select:
SELECT EXISTS(
    SELECT id
    FROM crawling
    WHERE title = #{title}
        AND url = #{url}
        AND site = #{site}
        AND created_at = #{createdAt}
)

update.foreach:
UPDATE crawling

    updated_at = #{crawling.crawlingAt}

WHERE title = #{crawling.title}
    AND url = #{crawling.url}
    AND site = #{crawling.site}
    AND created_at = #{crawling.createdAt}
