mapper.select:
SELECT id
FROM keyword
WHERE user_id = #{userId} AND name = #{name}

mapper.insert:
INSERT INTO keyword_site(keyword_id, site_name, created_at)
VALUES

    (
    #{keywordId},
    #{site, jdbcType=VARCHAR},
    #{createdAt}
    )
;

mapper.update:
UPDATE keyword
SET is_deleted = 1
WHERE user_id = #{userId}
    AND name = #{name};

UPDATE keyword_site
SET is_deleted = 1
WHERE keyword_id = (
        SELECT id
        FROM keyword
        WHERE user_id = #{userId}
          AND name = #{name}
    );

mapper.select:
SELECT ks.site_name
FROM keyword_site as ks
INNER JOIN keyword as k
ON k.id = ks.keyword_id
    AND k.user_id = #{userId}
    AND k.name = #{name};

SELECT id
FROM keyword
WHERE user_id = #{userId}
    AND name = #{name};

mapper.update:

    UPDATE keyword_site

        is_deleted = 1

    WHERE keyword_id = #{keywordId}
    AND site_name = #{site}
;

UPDATE keyword
SET name = #{modifiedKeyword.name}, is_important = #{modifiedKeyword.isImportant},
    alarm_mode = #{modifiedKeyword.alarmMode}, alarm_cycle = #{modifiedKeyword.alarmCycle},
    updated_at = #{createdAt}
WHERE id = (
    SELECT id
    FROM (
        SELECT id
        FROM keyword
        WHERE user_id = #{userId}
        AND name = #{name}
    ) AS sub_table
);
