mapper.select:
SELECT id
FROM keyword
WHERE name = #{keywordName}

SELECT id
FROM user_keyword
WHERE user_id = #{userId} AND keyword_id = #{keywordId}

mapper.update:
UPDATE user_keyword
SET is_deleted = 1
WHERE user_id = #{user_id}
    AND keyword_id = #{keyword_id}

UPDATE keyword
SET name = #{keyword_name}
WHERE id = #{keyword_id}
